mapper.insert:
INSERT INTO INSTRUCTOR (
    INSTRUCTOR_CODE
    , INSTRUCTOR_NAME
    , INSTRUCTOR_AGE
    , INSTRUCTOR_GENDER
    , INSTRUCTOR_PHONE
    , INSTRUCTOR_ADDR
) VALUES (
    #{instructorCode}
    , #{instructorName}
    , #{instructorAge}
    , #{instructorGender}
    ,

        #{phone}

    , #{instructorAddr}
)

INSERT INTO INSTRUCTOR_IMG (
    INSTRUCTOR_IMG_CODE
    , INSTRUCTOR_CODE
    , INSTRUCTOR_ORIGIN_FILE_NAME
    , INSTRUCTOR_ATTACHED_FILE_NAME
) VALUES (
    (SELECT 'INSTRUCTOR_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(INSTRUCTOR_CODE, 12))), 0) + 1, 3,'0')||'_IMG'
    FROM INSTRUCTOR_IMG)
    , #{instructorCode}
    , #{instructorImgVO.instructorOriginFileName}
    , #{instructorImgVO.instructorAttachedFileName}
)

mapper.update:
UPDATE INSTRUCTOR
SET INSTRUCTOR_PHONE = #{instructorPhone}
    , INSTRUCTOR_ADDR = #{instructorAddr}
WHERE INSTRUCTOR_CODE = #{instructorCode}
