mapper.insert:
INSERT INTO LECTURE (
    LECTURE_CODE
    , LECTURE_TITLE
    , LECTURE_SUBJECT
    , LECTURE_STUDENT
    , LECTURE_PERIOD
    , LECTURE_PRICE
    , INSTRUCTOR_CODE
) VALUES (
    (SELECT 'LECTURE_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(LECTURE_CODE, 9))), 0) + 1, 3,'0')
    FROM LECTURE)
    , #{lectureTitle}
    , #{lectureSubject}
    , #{lectureStudent}
    , #{lecturePeriod}
    , #{lecturePrice}
    , #{instructorCode}
)

INSERT INTO STUDENT (
    STUDENT_CODE
    , STUDENT_NAME
    , STUDENT_AGE
    , STUDENT_GENDER
    , STUDENT_TEL
    , STUDENT_ADDR
    , MEMBER_ID
    , LECTURE_CODE
) VALUES (
    (SELECT 'STUDENT_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(STUDENT_CODE, 9))), 0) + 1, 3,'0')
    FROM STUDENT)
    , #{studentName}
    , #{studentAge}
    , #{studentGender}
    , #{studentTel}
    , #{studentAddr}
    , #{memberId}
    , #{lectureCode}
)
