mapper.select:
SELECT MEMBER_ID
    , MEMBER_NAME
    , MEMBER_ROLL
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PW = #{memberPW}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO MEMBER (
    MEMBER_ID
    , MEMBER_PW
    , MEMBER_NAME
    , MEMBER_TEL
    , MEMBER_ADDR
    , ADDR_DETAIL
    , MEMBER_EMAIL
    , MEMBER_GENDER
) VALUES (
    #{memberId}
    , #{memberPW}
    , #{memberName}
    , #{memberTel}
    , #{memberAddr}
    , #{addrDetail}
    , #{memberEmail}
    , #{memberGender}
)

mapper.update:
UPDATE TABLE MEMBER SET
MEMBER_PW = #{memberPW}
, MEMBER_TEL = #{memberTel}
, MEMBER_ADDR = #{memberAddr}
, ADDR_DETAIL = #{addrDetail}
, MEMBER_EMAIL = #{memberEmail}
WHERE MEMBER_ID = #{memberId}
