mapper.insert:
INSERT INTO BOARD_QA_LIST(
QA_BOARD_NUM
, QA_BOARD_TITLE
, QA_BOARD_CONTENT
, QA_BOARD_WRITER
) VALUES(
(SELECT NVL(MAX(QA_BOARD_NUM), 0) + 1 FROM BOARD_QA_LIST)
, #{qaBoardTitle}
, #{qaBoardContent}
, #{qaBoardWriter}
)

INSERT INTO BOARD_FREE_COMMENTS (
COMMENT_ID
, COMMENT_CONTENT
, COMMENT_WRITER
, COMMENT_CREATE_DATE
, COMMENT_NUM
) VALUES (
(SELECT NVL(MAX(COMMENT_ID), 0) + 1 FROM BOARD_FREE_COMMENTS)
, #{commentContent}
, #{commentWriter}
, SYSDATE
, #{commentNum}
)

mapper.update:
UPDATE BOARD_QA_LIST
SET
QA_BOARD_TITLE = #{qaBoardTitle}
, QA_BOARD_CONTENT = #{qaBoardContent}
WHERE QA_BOARD_NUM = #{qaBoardNum}

UPDATE BOARD_FREE_COMMENTS
SET
COMMENT_CONTENT = #{commentContent}
WHERE COMMENT_ID = #{commentId}

mapper.insert:
INSERT INTO QA_IMG (
    QA_CODE
    , QA_ORIGIN_FILE_NAME
    , QA_ATTACHED_FILE_NAME
    , QA_BOARD_NUM
)

    SELECT (SELECT 'IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(IMG_CODE, 5))), 0) + 1 + #{idx}, 3, '0') FROM QA_IMG)
        , #{img.originFileName}
        , #{img.attachedFileName}
        , #{img.qaBoardNum}
    FROM DUAL
