mapper.insert:
INSERT INTO RENTAL_FACILITY (
    RENTAL_CODE
    , FACILITY_CODE
    , RENTAL_DATE
    , RENTAL_TIME_CODE
    , RENTAL_CHARGE
    , RENTAL_USER
    , RENTAL_TEAM
    , RENTAL_PURPOSE
    , RENTAL_STATUS
    , RENTAL_SIGN_CODE
)

    SELECT
         #{facility.rentalDate}||'_'||#{facility.rentalTimeCode}
            , #{facility.facilityCode}
            , #{facility.rentalDate}
            , #{facility.rentalTimeCode}
            , #{facility.rentalCharge}
            , #{facility.rentalUser}
            , #{facility.rentalTeam}
            , #{facility.rentalPurpose}
            , 1
            ,#{facility.rentalUser}||'_'||TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    FROM DUAL
